import React, {Component} from 'react';
import SearchBar from '../components/search/SearchBar'
import { Row, Col } from 'react-bootstrap';
import NavBar from "../components/navbar/AppNavBar";
import {UserInfo} from "../components/profilepage/userinfo/UserInfo";
import * as config from "../config.json";
import {search} from '../components/search/util';

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            images: []
        }
    }

    async componentDidMount() {
        const cConfig = config.default.config.links.tmdb;
        const movieImagesResults = await search(`${cConfig.discovermovie.link + cConfig.api_key + config.default.config.keys.tmdb + cConfig.discovermovie.query}`);
        const tvImagesResults = await search(`${cConfig.link + cConfig.discovertv.api_key + config.default.config.keys.tmdb + cConfig.discovertv.query}`);
        const movieImagesData = movieImagesResults[0].data.results;
        const tvImagesData = tvImagesResults[0].data.results;
        const movieImagesProps = movieImagesData.map(movie => ({
            src: `${cConfig.link + 'w300' + data.backdrop_path}`,
            thumbnail: `${cConfig.link + 'w780' + data.backdrop_path}`,
            thumbnailWidth: 300,
            thumbnailHeight: 250
        }));
        const tvImagesProps = movieImagesData.map(movie => ({
            src: `${cConfig.link + 'w300' + data.backdrop_path}`,
            thumbnail: `${cConfig.link + 'w780' + data.backdrop_path}`,
            thumbnailWidth: 300,
            thumbnailHeight: 250
        }));
        const imageProps = [].push(movieImagesProps.slice(10)).push(tvImagesProps.slice(10));
        this.setState({images: imageProps});
    }

    render() {
    return (

    <>
        <NavBar showLogin/>
        <div style={{
            
        }}>

        </div>
        <h1 style={{fontSize: "calc(4rem + 6vw)", position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -150%)", width: "100%", textAlign: "center"}}>MyEList</h1>
        <h3 style={{fontSize: "calc(1.1rem + 0.7vw)", position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -200%)", width: "100%", textAlign: "center"}}>Simply Search for any Movie/TV Show/Game!</h3>
        <div style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -60%)"}}>
            <SearchBar/>
        </div>
    </>
    )
  }
}

export default Home;
