import React from "react";
import {useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCircle} from "@fortawesome/free-solid-svg-icons";
import {Button, Form, Row, Col, Card, Image} from "react-bootstrap";
import './UserInfo.css';

export function UserInfo(props) {
    const user = useSelector(state => state.user);
    let numMovies, numTvs, numGames, numMedia;

    if (user.mediaLists)
        user.mediaLists.forEach(mediaList => {
        numMovies += mediaList.movies.length;
        numTvs += mediaList.tvs.length;
        numGames += mediaList.games.length;
    });

    numMedia = numMovies + numTvs + numGames;

    return (
        <>
            <Row className='align-items-center' style={{height: '60vh', backgroundColor: 'rgba(255,255,255,0.5)'}}>
                        <Col xs={2}>
                            <Image className='user-profile-picture' src={require('../../../images/defaultposter.jpg').default} />
                        </Col>
                        <Col xs={10}>
                            <Row className='user-profile-name'>
                                {user.username}
                            </Row>
                            <Row>
                                <Col xs={4}>
                                    <p className='user-media-num'>
                                        {'Movies: ' + numMovies}
                                    </p>
                                </Col>
                                <Col xs={4}>
                                    <FontAwesomeIcon className='circle-separator' icon={faCircle} />
                                    <p className='user-media-num'>
                                        {'Tvs: ' + numTvs}
                                    </p>
                                </Col>
                                <Col xs={4}>
                                    <FontAwesomeIcon className='circle-separator' icon={faCircle} />
                                    <p className='user-media-num'>
                                        {'Games: ' + numGames}
                                    </p>
                                </Col>
                            </Row>
                        </Col>
            </Row>
        </>
    )
}
