import Gallery from "react-grid-gallery";
import React, {useCallback, useEffect, useState} from "react";
import * as config from "../../../config.json";
import {search} from "../../search/util";

export function HomeGallery(props) {
    const [images, setImages] = useState([]);

    const formatDate = date => date.getFullYear() + '-' + (date.getMonth() < 9 ? '0' : '') + (date.getMonth() + 1) + '-' + (date.getDate() < 9 ? '0' : '') + date.getDate();
    const getFirstDate = date => (date.getMonth() <= 2 ? date.getFullYear() - 1 : date.getFullYear()) + '-' + (date.getMonth() < 9 ? '0' : '') + ((date.getMonth() <= 2 ? 11 : (date.getMonth() - 3)) + 1) + '-01';

    const getImages = useCallback(async() => {
        const cConfig = config.default.config.links.tmdb;

        const currDate = new Date();

        console.log(formatDate(currDate));
        console.log(getFirstDate(currDate));

        const movieImagesResults = await search(`${cConfig.discovermovie.link + cConfig.discovermovie.api_key + config.default.config.keys.tmdb + cConfig.discovermovie.query}`);
        const tvImagesResults = await search(`${cConfig.discovertv.link + cConfig.discovertv.api_key + config.default.config.keys.tmdb + cConfig.discovertv.query}`);
        console.log(`${cConfig.discovertv.link + cConfig.discovertv.api_key + config.default.config.keys.tmdb + cConfig.discovertv.query}`);
        if (!movieImagesResults || !tvImagesResults) return;
        const movieImagesData = movieImagesResults[0].data.results;
        const tvImagesData = tvImagesResults[0].data.results;
        const movieImagesProps = movieImagesData.filter(movie =>movie.backdrop_path).map(movie => ({
            src: `${cConfig.image.link + '/w500/' + movie.backdrop_path}`,
            thumbnail: `${cConfig.image.link + '/w500/' + movie.backdrop_path}`,
            thumbnailWidth: 300,
            thumbnailHeight: 169
        }));
        const tvImagesProps = tvImagesData.filter(tv => tv.backdrop_path).map(tv => ({
            src: `${cConfig.image.link + '/w500/' + tv.backdrop_path}`,
            thumbnail: `${cConfig.image.link + '/w500/' + tv.backdrop_path}`,
            thumbnailWidth: 300,
            thumbnailHeight: 169
        }));

        let movieIndex = 0, tvIndex = 0;

        const imageProps = movieImagesProps.slice(1,11).concat(tvImagesProps.slice(1,11));

        setImages(imageProps);
    }, []);

    useEffect(() => {
        getImages()
    }, [getImages]);

    return (
        <div style={{
            display: "block",
            minHeight: "1px",
            width: "100%",
            overflow: "auto",
            position: 'absolute',
            cursor: 'none'
        }}>
            <Gallery
                images={images}
                enableLightbox={false}
                enableImageSelection={false}/>
        </div>
    )
}
