import {useSelector} from "react-redux";
import {BarSeries, Chart} from "@devexpress/dx-react-chart";
import {ResponsiveEmbed, Row} from "react-bootstrap";
import {ResponsiveContainer, BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Legend, Bar} from "recharts";

export function MediaPlaytimeGraph(props) {

    const isMobile = /Android|webOS|iPhone|kindle|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);

    const mediaLists = useSelector(state => state.user.mediaLists);

    let moviePlaytimes = 0;
    let tvPlaytimes = 0;
    let gamePlaytimes = 0;

    if (mediaLists)
        mediaLists.forEach(mediaList => {
            mediaList.movies.forEach(movie => {
                moviePlaytimes += movie.playTime
            });
            mediaList.tvs.forEach(tv => {
                tvPlaytimes += tv.playTime
            });
            mediaList.games.forEach(game => {
                gamePlaytimes += game.playTime
            });
        });

    const getDayTime = (value) = Math.floor(value / 60 / 24);
    const getWeekTime = (value) = Math.floor(value / 60 / 24 / 7);
    const getMonthTime = (value) = Math.floor(getWeekTime(value) / 30);

    let movieWeektimes = Math.floor(moviePlaytimes / 60 / 24 / 7);
    moviePlaytimes -= movieWeektimes * 60 * 24 * 7;
    let movieDaytimes = Math.floor((moviePlaytimes / 60) / 24);
    moviePlaytimes -= movieDaytimes * 60 * 24;
    let movieHourtimes = Math.floor((moviePlaytimes / 60));
    moviePlaytimes -= movieHourtimes * 60;

    let tvDaytimes = Math.floor((tvPlaytimes / 60) / 24);
    tvPlaytimes -= tvDaytimes * 60 * 24;
    let tvHourtimes = Math.floor((tvPlaytimes / 60));
    tvPlaytimes -= tvHourtimes * 60;

    let gameDaytimes = Math.floor((gamePlaytimes / 60) / 24);
    gamePlaytimes -= gameDaytimes * 60 * 24;
    let gameHourtimes = Math.floor((gamePlaytimes / 60));
    gamePlaytimes -= gameHourtimes * 60;

    const mediaPlaytimes = [
        {name: 'Movies', days: movieDaytimes, hours: movieHourtimes, minutes: moviePlaytimes},
        {name: 'Tvs', days: tvDaytimes, hours: tvHourtimes, minutes: tvPlaytimes},
        {name: 'Games', days: gameDaytimes, hours: gameHourtimes, minutes: gamePlaytimes},
    ];

    console.log(mediaPlaytimes);

    return (
        <>
            <Row className="justify-content-center align-items-center" style={{
                height: '500px'
            }}>
                <ResponsiveContainer width={isMobile ? '90%' : '60%'} height='80%'>
                    <BarChart width={500} height={300} layout='vertical' barCategoryGap={5} data={mediaPlaytimes} margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 2,
                    }}>
                        <CartesianGrid />
                        <XAxis
                            type="number"
                            domain={[0, 'dataMax + 10']}
                        />
                        <YAxis
                            dataKey="name"
                            type="category"
                        />
                        <Tooltip />
                        <Legend />
                        <Bar dataKey="days"  stackId="a" fill="red" minPointSize={2} barSize={32} />
                        <Bar dataKey="hours" stackId="a" fill="blue" minPointSize={2} barSize={32} />
                        <Bar dataKey="minutes" stackId="a" fill="green" minPointSize={2} barSize={32} />
                    </BarChart>
                </ResponsiveContainer>
            </Row>
        </>
    )
}
