import {useSelector} from "react-redux";
import {BarSeries, Chart} from "@devexpress/dx-react-chart";
import {ResponsiveEmbed} from "react-bootstrap";
import {ResponsiveContainer, BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Legend, Bar} from "recharts";

export function MediaPlaytimeGraph(props) {

    const mediaLists = useSelector(state => state.user.mediaLists);

    let moviePlaytimes = 0;
    let tvPlaytimes = 0;
    let gamePlaytimes = 0;

    if (mediaLists)
        mediaLists.forEach(mediaList => {
            moviePlaytimes += mediaList.movies.length !== 0 && mediaList.movies.reduce((curr, acc) => curr.playTime + acc);
            tvPlaytimes += mediaList.tvs.length !== 0 && mediaList.tvs.reduce((curr, acc) => curr.playTime + acc);
            gamePlaytimes += mediaList.games.length !== 0 && mediaList.games.reduce((curr, acc) => curr.playTime + acc);
        });

    const mediaPlaytimes = [
        {name: 'Movies', playtime: moviePlaytimes},
        {name: 'Tvs', playtime: tvPlaytimes},
        {name: 'Game', playtime: gamePlaytimes},
    ];

    return (
        <>
            <ResponsiveContainer width="100%" height="100%">
                <BarChart width={150} height={40} data={mediaPlaytimes}>
                    <Bar dataKey="playtimes" fill="#8884d8" />
                </BarChart>
            </ResponsiveContainer>
        </>
    )
}
