import React from "react";
import {useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCircle} from "@fortawesome/free-solid-svg-icons";
import {Button, Form, Row, Col, Card, Image} from "react-bootstrap";
import './UserInfo.css';

export function UserInfo(props) {
    const user = useSelector(state => state.user);
    let numMovies = 0, numTvs = 0, numGames = 0, numMedia = 0;
    let allMovies = [], allTvs = [], allGames = [];
    if (user.mediaLists)
        user.mediaLists.forEach(mediaList => {
            if (mediaList.all) return;
            numMovies += mediaList.movies.length;
            numTvs += mediaList.tvs.length;
            numGames += mediaList.games.length;
    });

    numMedia = numMovies + numTvs + numGames;

    return (
        <>
            <Row className='align-items-center' style={{height: '60vh', backgroundColor: 'rgba(255,255,255,0.5)'}}>
                        <Col xs={2}>
                            <Image className='user-profile-picture' src={require('../../../images/default_profile.png').default} />
                        </Col>
                        <Col xs={10}>
                            <Row className='user-profile-name'>
                                {user.username}
                            </Row>
                            <Row>
                                <Col xs={12} ml={6} xl={4}>
                                    <Row>
                                        <Col xs={3}>
                                            <Row>
                                                <p className='user-media-num-title'>
                                                    {'Media'}
                                                </p>
                                                <p className='user-media-num-value'>
                                                    {numMedia}
                                                </p>
                                            </Row>
                                        </Col>
                                        <Col xs={3}>
                                            <p className='user-media-num-title'>
                                                {'Movies'}
                                            </p>
                                            <p className='user-media-num-value'>
                                                {numMovies}
                                            </p>
                                        </Col>
                                        <Col xs={3}>
                                            <p className='user-media-num-title'>
                                                {'Tvs'}
                                            </p>
                                            <p className='user-media-num-value'>
                                                {numTvs}
                                            </p>
                                        </Col>
                                        <Col xs={3}>
                                            <p className='user-media-num-title'>
                                                {'Games'}
                                            </p>
                                            <p className='user-media-num-value'>
                                                {numGames}
                                            </p>
                                        </Col>
                                    </Row>
                                </Col>
                            </Row>
                        </Col>
            </Row>
        </>
    )
}
