import React, {Component} from 'react';
import SearchBar from '../components/search/SearchBar'
import { Row, Col } from 'react-bootstrap';
import NavBar from "../components/navbar/AppNavBar";
import {UserInfo} from "../components/profilepage/userinfo/UserInfo";
import * as config from "../config.json";
import {search} from '../components/search/util';
import Gallery from 'react-grid-gallery';

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            images: []
        }
    }

    async componentDidMount() {
        const cConfig = config.default.config.links.tmdb;
        const movieImagesResults = await search(`${cConfig.discovermovie.link + cConfig.discovermovie.api_key + config.default.config.keys.tmdb + cConfig.discovermovie.query}`);
        const tvImagesResults = await search(`${cConfig.discovertv.link + cConfig.discovertv.api_key + config.default.config.keys.tmdb + cConfig.discovertv.query}`);
        if (!movieImagesResults || !tvImagesResults) return;
        const movieImagesData = movieImagesResults[0].data.results;
        const tvImagesData = tvImagesResults[0].data.results;
        const movieImagesProps = movieImagesData.map(movie => ({
            src: `${cConfig.image.link + '/w300/' + movie.backdrop_path}`,
            thumbnail: `${cConfig.image.link + '/w780/' + movie.backdrop_path}`,
            thumbnailWidth: 300,
            thumbnailHeight: 169
        }));
        const tvImagesProps = tvImagesData.map(tv => ({
            src: `${cConfig.image.link + '/w300/' + tv.backdrop_path}`,
            thumbnail: `${cConfig.image.link + '/w780/' + tv.backdrop_path}`,
            thumbnailWidth: 300,
            thumbnailHeight: 169
        }));

        const imageProps = movieImagesProps.concat(tvImagesProps)

        this.setState({images: imageProps});
    }

    render() {
    return (

    <>
        <NavBar showLogin/>
        <div style={{
            display: "block",
            minHeight: "1px",
            width: "100%",
            overflow: "auto",
            position: 'absolute',
            cursor: 'none'
        }}>
            <Gallery
                images={this.state.images}
                enableLightbox={false}
                enableImageSelection={false}/>
        </div>
        <h1 style={{
            fontSize: "calc(4rem + 6vw)",
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -120%)",
            width: "100%",
            textAlign: "center",
            textShadow: 'rgb(255, 255, 255) -1px -1px 0px, rgb(255, 255, 255) 1px -1px 0px, rgb(255, 255, 255) -1px 1px 0px, rgb(255, 255, 255) 1px 1px 0px',
            color: '#e6e6e6'
        }}>
            MyEList
        </h1>
        <h3 style={{
            fontSize: "calc(1.1rem + 0.7vw)",
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -90%)",
            width: "100%",
            textAlign: "center",
            fontWeight: 'normal',
            textShadow: 'rgb(255, 255, 255) -1px -1px 0px, rgb(255, 255, 255) 1px -1px 0px, rgb(255, 255, 255) -1px 1px 0px, rgb(255, 255, 255) 1px 1px 0px',
            color: '#e6e6e6'
        }}>
            Simply Search for any Movie/TV Show/Game!
        </h3>
        <div style={{position: "fixed", top: "50%", left: "50%", transform: "translate(-50%, 1000%)"}}>
            <SearchBar/>
        </div>
    </>
    )
  }
}

export default Home;
