import React, {Component} from 'react';
import SearchBar from '../components/search/SearchBar'
import { Row, Col } from 'react-bootstrap';
import NavBar from "../components/navbar/AppNavBar";
import {UserInfo} from "../components/profilepage/userinfo/UserInfo";
import * as config from "../config.json";
import {search} from '../components/search/util';

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            images: []
        }
    }

    async componentDidMount() {
        const mConfig = config.default.config.links.tmdb.discovermovie;
        const tvConfig = config.default.config.links.tmdb.discovertv;
        const movieImagesResults = await search(`${mConfig.link + mConfig.api_key + config.default.config.keys.tmdb + mConfig.query}`);
        const tvImagesResults = await search(`${tvConfig.link + tvConfig.api_key + config.default.config.keys.tmdb + tvConfig.query}`);
        let movieImagesData = movieImagesResults[0].data.results;
        let tvImagesData = tvImagesResults[0].data.results;
    }

    render() {
    return (

    <>
        <NavBar showLogin/>
        <div style={{

        }}>

        </div>
        <h1 style={{fontSize: "calc(4rem + 6vw)", position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -150%)", width: "100%", textAlign: "center"}}>MyEList</h1>
        <h3 style={{fontSize: "calc(1.1rem + 0.7vw)", position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -200%)", width: "100%", textAlign: "center"}}>Simply Search for any Movie/TV Show/Game!</h3>
        <div style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -60%)"}}>
            <SearchBar/>
        </div>
    </>
    )
  }
}

export default Home;
